mapper.select:
select

from sd_conference
where project = #{project,jdbcType=VARCHAR}
and start_time >= #{startTime,jdbcType=TIMESTAMP}
and start_time <= #{endTime,jdbcType=TIMESTAMP}
and status = #{status,jdbcType=TINYINT}

select

from sd_conference


    room_id = #{roomId,jdbcType=VARCHAR}


    and project = #{project,jdbcType=VARCHAR}


    and status = #{status,jdbcType=TINYINT}


    and start_time >= #{from,jdbcType=TIMESTAMP}


    and start_time <= #{to,jdbcType=TIMESTAMP}


    and user_id in (
    select user_id from sd_user_dept where dept_id in

      #{item,jdbcType=VARCHAR}

    )


order by start_time desc
limit #{limit,jdbcType=INTEGER} offset #{offset,jdbcType=BIGINT}

