mapper.select:
select sud.id,sud.user_id userId,sud.serial_number serialNumber,sud.project,sud.create_time createTime, sud.update_time updateTime,sd.device_name deviceName from sd_user_dev sud left join sd_device sd on sud.serial_number = sd.serial_number
and user_id = #{userId,jdbcType=BIGINT}
and serial_number = #{serialNumber,jdbcType=VARCHAR}

mapper.insert:
insert into sd_user_dev (id, user_id, serial_number,
  project, create_time
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{serialNumber,jdbcType=VARCHAR},
  #{project,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
